import { Component } from "react";
import Body from "./Body/Body";
import Head from "./Head/Head";

import "./HomePage.css"

class HomePage extends Component
{
    _id:string|number = ""
    state = {
        displaySenderInformation : false,
        receiverProfile : {
            id : this._id,
            email: "",
            name: "",
            category: "",
            image: ""
        },
        senderProfile : {
            id : 2,
            email: "bertrandovide@hotmail.com",
            name: "Ovide Bertrand",
            category: "person",
            image: ""
        }
    }
    props:any = this.props

    getReceiverInformation = (email:string, id:number|string, name:string, category:string|number) => {
        if(email !== "" && id !== "" && name !== "" && category !== null)
        this.setState({data: {email, id, name, category}, displaySenderInformation: true})
    }

    render(){
        return (
            <div className="home-page">
                <Head disPlaySenderInformation = {this.state.displaySenderInformation} receiverProfile = {this.state.receiverProfile} senderProfile = {this.state.senderProfile} />
                <Body getReceiverInformation = {this.getReceiverInformation} />
            </div>
        )
    }
}

export default HomePage
