import { Component } from 'react'

import close from "./../../../wwwroot/images/times_dark.png";
import camera from "./../../../wwwroot/images/photo.png";
import pauseCircular from "./../../../wwwroot/images/pause_circular.png";
import pause from "./../../../wwwroot/images/pause.png"
import stop from "./../../../wwwroot/images/stop_color.png"
import stopCircular from "./../../../wwwroot/images/stop_circular.png"
import record from "./../../../wwwroot/images/record_circular.png"



import "./RenderFileZone.css"

class RenderFileZone extends Component {
    props:any = this.props 
    state = {
      isRecording: false
    }

    closeRenderZone = (event:any) => {
        var renderZone = document.getElementById("display-media-box")
        renderZone?.style.setProperty("display", "none")
    }

    recordVideo = (event:any) =>
    {
      var isRecording = !this.state.isRecording
      this.setState({isRecording})
    }

    stopRecordVideo = (event: any) => {
      var isRecording = false
      this.setState({isRecording})
    }

    render() {
        return (
            <div id = "display-media-box">
                <div id = "render-zone">
                  <div id = "render-zone-title-bar"><span>title here</span> <img src={close} alt="close" onClick={this.closeRenderZone} /></div>
                  <div className="render-zone-body">
                    <div className="render-zone-display">
                        <div className="render-record-time">
                            <div className="record-icon"> <div></div> </div>
                            <span className="record-time">00:00</span>
                        </div>
                        <canvas id="render-zone-canvas">
                        </canvas>
                        <div className="render-zone-menu">
                            <img src={(this.state.isRecording)?pauseCircular:record} onClick = {this.recordVideo} alt="start-pause" />
                            <img src={stop} onClick = {this.stopRecordVideo} alt="stop" />
                            <img src={camera} alt="take pic" />
                        </div>
                    </div>
                    <div className="file-manager">
                        file here
                    </div>
                  </div>
                </div>
              </div>
        )
    }
}

export default RenderFileZone
