import { Component } from "react";

import "./Discussion.css"

import discussions from "./conversation.json"
import Conversation from "./Conversation/Conversation";

class Discussion extends Component
{
    id:any = -1
    state = {
        discussions,
        prevSelectedDiscussionId:-1
    }

    setFocus = (event:any) => {
        let prevSelectedDiscutionId = this.state.prevSelectedDiscussionId;
        if(prevSelectedDiscutionId !== -1 )
        {
            let element = document.getElementById(`discussion-${prevSelectedDiscutionId}`)
            .classList.remove("selected-discussion")
        }
            // this.
        this.set
        prevSelectedDiscution = event.currentTarget
        // console.log(prevSelectedDiscution)
        prevSelectedDiscution.classList.add("selected-discution")
        // console.log(typeof prevSelectedDiscution)
        this.element = prevSelectedDiscution

    }

    render() {
        let discutionList = this.state.discussions.map((data) => <Conversation key={data.id} onClick={this.setFocus} data = {data}/>)
        return (
            <div className="discussion">
                {discutionList}
            </div>
        )
    }
}

export default Discussion
