import { Component, FormEvent } from "react";

import "./ActionBar.css"

class ActionBar extends Component{

    state = {
        fieldValue:""
    }

    handleChange = (event:any) => {
        this.setState({fieldValue: event.currentTarget.value??""})
    }

    handleFocus = (event:any) =>{
        console.log(event.currentTarget.style)
        event.currentTarget.style = "{text-align:left}"
    }


    render() {
        return (
            <div className="action-bar col col-sm-12">
                <div className="search-icon"><span>img</span></div>
                <div className="col col-sm-10 search-field"><input type="text" className="form-control" onFocus={this.handleFocus} onChange={this.handleChange} value={this.state.fieldValue} placeholder="Search discussion here" /></div>
                <div className="menu-icon"><span className="" >...</span></div>
            </div>
        )
    }
}

export default ActionBar
