import {Component} from 'react'

import "./TextFormField.css"

class TextFormField extends Component
{
    fieldType:any = ""
    placeHolder:any = "";
    state = {
        fieldValue:""
    }
    
    constructor(props:any)
    {
        super(props)
        this.fieldType = "text" //props.fieldType
        this.placeHolder = "Write something ere" //props.placeHolder

        // this.state = {
        //     fieldValue : props.fieldValue
        // }

    }
    
    handleChange = (event:any) => {
        this.setState({text:event.currentTarget.value})
    }

    render(){
        return (
            <div className = "form-group row text-form-field">
                <div className = "col-sm-12">    
                    <span>Error message</span>
                </div>
                <div className = "col-sm-12">    
                    <fieldset className = "col-sm-12">
                        <span>img</span>
                        <input type={this.fieldType} className="form-control border-dark border-1" placeholder={this.placeHolder} value={this.state.fieldValue} onChange={this.handleChange} />
                    </fieldset>
                </div>
            </div>
        );
    };
}

export default TextFormField
