import { Component } from "react";

import {Link} from "react-router-dom";
  

import LoginForm from "./LoginForm/LoginForm";
import FormMessage from "../../shared/formmessage/FormMessage";

import PageName from '../../shared/pagename/PageName';
import {VerticalLogo} from "../../shared/logo/Logos";
  
import './LoginPage.css'

class LoginPage extends Component{

    render(){
        return (
            <div className = "login-page" key="login-page">
                <div className="title" key="login-page-name-section">
                    <PageName name="Login" key = "login-page-name"/>
                </div>
                <div className = "content" key="login-page-tools-sections">
                    <VerticalLogo key = "login-page-logo"/>
                    <FormMessage type="error" message="Use either phone or email" key = "login-page-alert-message"/>
                    <LoginForm key = "login-page-form"/>
                    <Link to = './../HomePage' key = "login-page-link1" className = "fs-5 fw-bold link-secondary">{'>'} Home {'<'}</Link>
                    <Link to = './../ForgotPasswordPage' key = "login-page-link1" className = "fs-5 fw-bold link-secondary">{'>'} Forgot Password {'<'}</Link>
                    <Link to="./../RegisterPage" key = "login-page-link2" className="fs-4 fw-bold link-secondary">{'>>'} Create Account {'<<'}</Link>
                </div>
            </div>
        )
    }
}

export default LoginPage
