import { Component } from "react";
import LeftSide from "./LeftSide/LeftSide";
import RightSide from './RightSide/RightSide';
import "./Body.css";

// import messageList from "./../../../../wwwroot/data/messages.json"
import { MessageDTO } from "../../../shared/utils/Types";

class Body extends Component
{
    props:any = this.props
    _msgs: MessageDTO [] = [] 
    _category:string|number = ""
    state = {
        messages:this._msgs,
        identity:"",
        id:"",
        name:"",
        category: this._category
    }

    //method to get the list of message in a chat room


    openConversation = (identity:string, id:number|string, userChatRoomId:number|string, name:string, category:string|number) => {
        
        // getAllChatRomMessage 
        let messages = this.state.messages.filter(data => filterCondition(data, id, identity))
        //map if necessaire 
        this.setState({messages, identity, id, name, category})
        try {
        
            this.props.getReceiverInformation(identity, id, name, category)
        } catch (error) {}
    }



    onSendMessage = (message="", file:any="", name = "", category="person")=>{
        let date = new Date()
        let datetime = `${date.getFullYear()}-${date.getMonth()}-${date.getDay()}T${date.getHours()}:${date.getMinutes()}:${date.getSeconds()}`
        let messages = [...this.state.messages];
        let newMessage: MessageDTO = {
            identity: this.state.identity,
            id: this.state.id,
            file: file,
            name: name,
            message: message,
            isRead: false,
            isSend: false,
            isReceive: false,
            isIncomingMessage: false,
            dateTime:datetime,
            category: category
        }
        messages.push(newMessage)
        this.setState({messages})
    }

    render(){
        let messages = this.state.messages;
        messages = messages.sort((data1, data2) => {
                let value1 = (new Date(data1.dateTime)).getTime();
                let value2 = (new Date(data2.dateTime)).getTime();
                if(value1<value2) return -1;
                return 1;
             }
            )
        return(
            <div className="homepage-body">
                <LeftSide openConversation = {this.openConversation} key="home-page-right-side"/>
                <RightSide chatIsSelected = {this.state.identity !== "" || this.state.id !== "" } messages = {messages} onSendMessage={this.onSendMessage} name={this.state.name} category = {this.state.category} key="home-page-leftside"/>
            </div>
        )
    }
}

function filterCondition(data: { identity: string; id: string|number; file: string|Blob; name: string; message: string; isRead: boolean; isSend: boolean; isReceive: boolean; isIncomingMessage:boolean, dateTime: string; category: string; }, id: string | number, email: string): unknown {
    return (data.id === id || data.identity.toLowerCase().trim() === email.toLowerCase().trim());
}

export default Body
