import { Component } from "react";

import "./ListConversation.css"

import discussions from "./conversation.json"
import Conversation from "./Conversation/Conversation";

class ListConversation extends Component
{

    element:any = null
    props:any = this.props
    state = {
        discussions,
        prevSelectedDiscussion:this.element
    }

    setFocus = (event:any, id:number|string, email:string, category:number|string) => {
        let prevSelectedDiscussion = this.state.prevSelectedDiscussion
        if(prevSelectedDiscussion !== null )
            prevSelectedDiscussion.classList.remove("selected-discussion")
        
        prevSelectedDiscussion = event.currentTarget
        prevSelectedDiscussion.classList.add("selected-discussion")
        this.setState({prevSelectedDiscussion})

        try {
            this.props.openDiscussion(email, id, category)   
        } catch (error) {}
    }

    render() {
        let discutionList = this.state.discussions.map((data) => <Conversation key={data.id} onClick={(event:any) => this.setFocus(event, data.id, data.email, data.category)} data = {data}/>)
        return (
            <div className="discussion">
                {discutionList}
            </div>
        )
    }
}

export default ListConversation
