import {Component} from 'react'

import "./TextFormField.css"

class TextFormField extends Component
{
    fieldType:any = ""
    placeHolder:any = "";
    state = {
        fieldValue:""
    }
    
    constructor(props:any)
    {
        super(props)
        this.fieldType = props.fieldType
        this.placeHolder = props.placeHolder

        this.state = {
            fieldValue : props.fieldValue
        }
    }
    
    handleChange = (event:any) => {
        this.setState({text:event.currentTarget.value})
    }

    render(){
        return (
            <div className = "text-form-field">
                <div className = "col-sm-6">    
                    <fieldset>
                        <span>img</span>
                        <input type={this.fieldType} className="form-control" placeholder={this.placeHolder} value={this.state.fieldValue} onChange={this.handleChange} />
                    </fieldset>
                </div>
            </div>
        );
    };
}

export default TextFormField
