import { Component } from 'react'
import { getFileSize, getMediaType } from '../utils/functions'

import "./FileDisplayer.css"

class FileDisplayer extends Component {
    props:any = this.props
    state = {
        mediaIsPlaying: false,
        mediaIsDownloaded: false
    }

    render() {    
        
        var {name, size, fileSrc} = this.props
        var name1 =  name
        var mediaTypeExt = getMediaType(name1)
        var [mediaType, ext]  = mediaTypeExt.split("/")
        var file:any = {
            "video":<video src={require(name1)?.default} controls></video>, 
            "audio":<audio src={require(name1)?.default} controls></audio>,
            "image": <img src= {require(name1)?.default} alt="failed to load" />, 
            "application":<embed src= {require(name1)?.default} type= {mediaTypeExt}></embed>, 
            "unknow": <div className="unknow-file"><span>{`.${ext}`}</span><span>{name1}</span></div>
        }    // frameBorder={0}
        
        var media = file[mediaType]
        media = (media === undefined)?null:media
        return (
            <div className="file-displayer">
                {media}
                <div className = "file-description">
                    <span >{ext.toLocaleUpperCase()}</span>
                    <span>{getFileSize(size)}</span>
                </div>
                
            </div>
        )
    }
}

export default  FileDisplayer
