import { Component } from "react";
import Body from "./Body/Body";
import Head from "./Head/Head";
import Cookies from 'universal-cookie';
import { sendRequest } from "../../shared/utils/functions";

import "./HomePage.css"

class HomePage extends Component
{
    cookies = new Cookies()
    _id:string|number = ""
    state = {
        displayReceiverInformation : false,
        receiverProfile : {
            id : this._id,
            email: "",
            name: "",
            category: "",
            image: ""
        },
        senderProfile : {
            id : this.cookies.get("userId"),
            email: this.cookies.get("userEmail"),
            name: this.cookies.get("userName"),
            category: "person",
            image: ""
        }
    }
    
    
    props:any = this.props
    sucessGetMessages = (response:any) =>{
        console.log(response)
    }
    errorGetMessage = (error:any) =>{

    }

    loadData = () => {
        let userId = this.cookies.get("userId")
        sendRequest("https://www.estenligne.com:44364/api/UserProfile", "GET", {userId: userId}, this.sucessGetMessages, this.errorGetMessage)
        // sendRequest("https://www.estenligne.com:44364/api/Message/GetMany", "GET", {userId: userId}, this.sucessGetMessages, this.errorGetMessage)
    }

    componentWillMount = () => {
        this.loadData()
    }

    componentDidUpdate = () => {
        this.loadData()
        return 
    }
    
    getReceiverInformation = (email:string, id:number|string, name:string, category:string|number) => {
        if(email !== "" || id !== "" || name !== "")
        {
            this.setState({displayReceiverInformation: true})
            this.setState({receiverProfile: {email, id, name, category, image : ""}})
        }
    }

    render(){
        return (
            <div className="home-page">
                <Head displayReceiverInformation = {this.state.displayReceiverInformation} receiverProfile = {this.state.receiverProfile} senderProfile = {this.state.senderProfile} />
                <Body getReceiverInformation = {this.getReceiverInformation} />
            </div>
        )
    }
}

export default HomePage
