import { Component } from "react";
import Message from "./Message/Message";

import "./ListMessage.css"

class ListMessage extends Component{
    props:any = this.props

    render(){
        console.log(this.props.messages)
        let messages = this.props.messages.map((data:any) => <Message key={"message" + (new Date()).getTime() + Math.random()} category={data.type} type = {(data.file==="")?"text":"file"}  
                                                                incomingMessage = {data.incomingMessage}
                                                                message = {data.message} 
                                                                time = {getMessageTime(`${data.dateTime}`)}  
                                                                status = {getMessageSendingStatus(data.isIncomingMessage, data.isSend, data.isReceive)} />)
        return (
            <div className="list-message">
                {messages}
            </div>
        )
    }
}

export default ListMessage

function getMessageTime(datetime:string) {
    console.log(datetime)
    let time = datetime.split("T")[1]
    let to = time.lastIndexOf(":")
    return time.substring(0, to);
}

function getMessageSendingStatus(isIncomingMessage: boolean, isSend:boolean, isReceive:boolean)
{
    if(isIncomingMessage && isSend) return "send";
    else if(isIncomingMessage && isReceive) return "received"
    else return "not-send"
}
