import { Component } from "react";

import "./Message.css"

class Message extends Component{
    props:any = this.props
    render(){
        let {type, isIncomingMessage, message, time, status} = this.props
        return (
            <div className ={`prev-message ${(isIncomingMessage===true)?"incoming-message":"outgoing-message"}`}>
                <div className="prev-message-content">
                    {(type === "file")?<div></div>:<span>{message}</span>}
                </div>
                <div className="sending-information">
                    <span className="send-datetime">{time}</span>
                    {
                        (isIncomingMessage === true)?null:<span className="send-image">
                           <img src={outCommingsendImage(status)} alt="" />
                        </span>
                    }
                </div>
            </div>
        )
    }
}

export default Message

function outCommingsendImage(status:string) {
        switch (status) {
            case "send":
                return "send-image";
            case "received":
                return "receive-image";
            default:
                return "failed-image";
    };
}
