import { Component } from "react";
import "./FormMessage.css"

class FormMessage extends Component{
    message = "Message here"
    type = "warning"

    constructor(message="Message here", type="warning")
    {
        super({message, type})
        this.message = message
        this.type = type
    }

    render(){
        let textColor = "text-" + ((""+this.type).toLowerCase() || "info") 
        return (<span className={`${textColor} fs-4 fw-bold`}>{this.message}</span>)
    }
}

export default FormMessage
