import { Component } from "react";
import Body from "./Body/Body";
import Head from "./Head/Head";
import Cookies from 'universal-cookie';
import { sendRequest } from "../../shared/utils/functions";

import "./HomePage.css"

class HomePage extends Component
{
    cookies = new Cookies()
    _id:string|number = ""
    state = {
        displayReceiverInformation : false,
        receiverProfile : {
            id : this._id,
            email: "",
            name: "",
            category: "person",
            image: ""
        },
        senderProfile : {
            id : this._id,
            email: "",
            name: "",
            category: "person",
            image: ""
        }
    }
    
    
    props:any = this.props
    sucessGetMessages = (response:any) =>{
        console.log(response)
    }
    errorGetMessage = (error:any) =>{

    }

    loadData = () => {
        let userId = this.cookies.get("userId")
        sendRequest(`https://www.estenligne.com:44364/api/UserProfile?id=${userId}`, "GET", {userId: userId}, this.sucessGetMessages, this.errorGetMessage)
        // sendRequest("https://www.estenligne.com:44364/api/Message/GetMany", "GET", {userId: userId}, this.sucessGetMessages, this.errorGetMessage)
    }

    submitFunction(url:string, type:string, successFunction:Function, errorFunction:Function)
    {
        fetch(url, {
            "method": type,
            "credentials": 'include',
            "mode": 'no-cors',
            "headers": {
                "content-type": "application/json",
                "accept": "application/json"
            },
            }).then(response => response.json())
            .then(
                (result) => {
                    console.log(result)
                    successFunction(result)
                },
                (error) => {
                    errorFunction(error)
                }
            );
    }

    successGetUserProfile = (result:any) => {
        var keys = Object.keys(result)
        if(keys.includes('id') && keys.includes("username"))
        {
            this.setState({senderProfile : {id: result.id, name:result.name, email:result.identity, category:"person", image:""}})
        }
        else
        {
            this.setState({senderProfile : {
                id : this.cookies.get("userId"),
                email: this.cookies.get("userEmail"),
                name: this.cookies.get("userName"),
                category: "person",
                image: ""
            }})
        }
    }
    errorGetUserProfile = (error:any) => {
        console.log(error)
    }

    componentWillMount = () => {
        // this.loadData()
        
        let userId = this.cookies.get("userId")
        this.submitFunction(`https://www.estenligne.com:44364/api/UserProfile?id=${userId}`, "GET", this.successGetUserProfile, this.errorGetUserProfile)
    }

    componentDidUpdate = () => {
        let userId = this.state.senderProfile.id || this.cookies.get("userId")
        this.submitFunction(`https://www.estenligne.com:44364/api/ChatRoom/GetAll?id=${userId}`, "GET", (result:any)=>{console.log(result)}, (error:any)=>{console.log(error)})
        console.log('updateComponent')
    }

    getReceiverInformation = (email:string, id:number|string, name:string, category:string|number) => {
        if(email !== "" || id !== "" || name !== "")
        {
            this.setState({displayReceiverInformation: true})
            this.setState({receiverProfile: {email, id, name, category, image : ""}})
        }
    }

    render(){
        return (
            <div className="home-page">
                <Head displayReceiverInformation = {this.state.displayReceiverInformation} receiverProfile = {this.state.receiverProfile} senderProfile = {this.state.senderProfile} />
                <Body getReceiverInformation = {this.getReceiverInformation} />
            </div>
        )
    }
}

export default HomePage
