import { Component } from "react";

import "./Discussion.css"

import discussions from "./conversation.json"
import Conversation from "./Conversation/Conversation";

class Discussion extends Component
{
    prevSelectedDiscussion:any = null;
    state = {
        discussions,
        prevSelectedDiscussion:this.prevSelectedDiscussion
    }

    setFocus = (event:any) => {
        let prevSelectedDiscution = this.state.prevSelectedDiscussion;
        if(prevSelectedDiscution !== null)
            prevSelectedDiscution.classList.remove("selected-discussion")
        prevSelectedDiscution = event.currentTarget
        prevSelectedDiscution.classList.add("selected-discution")
        this.setState({prevSelectedDiscution})

    }

    render() {
        let discutionList = this.state.discussions.map((data) => <Conversation key={data.id} isFocused data = {data}/>)
        return (
            <div className="discussion">
                {discutionList}
            </div>
        )
    }
}

export default Discussion
