import { Component } from "react";

import "./ListConversation.css"

import conversations from "./../../../../../../wwwroot/data/conversation.json"
import Conversation from "./Conversation/Conversation";

class ListConversation extends Component
{

    element:any = null
    props:any = this.props
    state = {
        conversations,
        prevSelectedConversation:this.element
    }

    setFocus = (event:any, id:number|string, email:string, category:number|string) => {
        let prevSelectedConversation = this.state.prevSelectedConversation
        if(prevSelectedConversation !== null )
            prevSelectedConversation.classList.remove("selected-conversation")
        
        prevSelectedConversation = event.currentTarget
        prevSelectedConversation.classList.add("selected-conversation")
        this.setState({prevSelectedConversation})
        
        this.props.openConversation(email, id, category)
        try {   
        } catch (error) {}
    }

    render() {
        this.state.conversations.sort( data => (new Date(data.datetime)).getTime())
        let discutionList = this.state.conversations.map((data) => <Conversation key={data.id} onClick={(event:any) => this.setFocus(event, data.id, data.email, data.category)} data = {data}/>)
        return (
            <div className="list-conversation">
                {discutionList}
            </div>
        )
    }
}
export default ListConversation
