import { Component } from "react";

import "./Discussion.css"

import discussions from "./conversation.json"
import Conversation from "./Conversation/Conversation";

class Discussion extends Component
{
    state = {
        discussions,
        prevSelectedDiscussionId:""
    }

    setFocus = (id:any) => {
        let prevSelectedDiscutionId = this.state.prevSelectedDiscussionId
        console.log(prevSelectedDiscutionId)
        if(prevSelectedDiscutionId !== "" )
            document.getElementById(prevSelectedDiscutionId)?.classList.remove("selected-discussion")
        
        prevSelectedDiscutionId = id
        document.getElementById(id)?.classList.add("selected-discussion")
        this.setState({prevSelectedDiscutionId})
    }

    render() {
        let discutionList = this.state.discussions.map((data) => <Conversation key={data.id} id = {`discussion-${data.id}`} onClick={()=>this.setFocus(`discussion-${data.id}`)} data = {data}/>)
        return (
            <div className="discussion">
                {discutionList}
            </div>
        )
    }
}

export default Discussion
