import {Component} from "react";

import {Redirect} from 'react-router-dom';

import PageName from "../../shared/pagename/PageName";

import FormMessage from "../../shared/formmessage/FormMessage";
import UserProfileForm from "./UserProfileForm/UserProfileForm";
import {UserProfile} from "../../shared/utils/Types";
import {postOrUpdateRequest} from "../../shared/utils/functions";
import Cookies from "universal-cookie/es6";
import settings from './../../../settings.json';

import "./UserProfilePage.css";


class UserProfilePage extends Component{
    
    cookies = new Cookies()
    settings = settings
    props:any = this.props
    _alertMessage = "Use either email or phone number"
    _alertType = "error"
    
    state = {
        userName:"",
        profileIsSetted: false,
        alertMessage: this._alertMessage,
        alertType: this._alertType
    }

    successFunction = (result:any) =>
    {
        var keys = Object.keys(result)
        if(keys.includes("userProfileId"))
        {
            let date = new Date()
            let expirationDate = new Date()
            expirationDate.setMinutes(date.getMinutes() + 7 * 24 * 60)

            this.cookies.set("userProfileId", result.userProfileId, {sameSite: true, expires:expirationDate })                        
            this.setState({profileIsSetted: true, alertMessage:''})
        }
        else
            this.setState({profileIsSetted: false, alertMessage:result})
    }

    errorFunction = (error:any) =>
    {
        this.setState({profileIsSetted: false,  alertMessage: `Something append wrong ${error}`})
    }

    uploadImage = (data:File) =>
    {

        return true;
    }

    submitForm = (data:UserProfile) => {
        var newData = {about: data.about??"", username:data.name, identity:data.identity} 
        postOrUpdateRequest(`${this.settings.host}/api/userprofile`, "POST", newData, this.cookies.get("token"), this.successFunction, this.errorFunction)
    }

    render(){
        return(
            <div className = "profile-page" >
                <div className = "title">
                    <PageName name = "Profile Page" key = "profilepage-name"/>    
                </div>
                
                <div className = "content">
                    {/* <VerticalLogo key = "register-page-logo"/> */}
                    <FormMessage type={this.state.alertType} message = {this.state.alertMessage} key = "profilepage-alert-message"/>
                    <UserProfileForm uploadImage={this.uploadImage} submitForm = {this.submitForm} key = "profilepage-form" />
                </div>
                {(this.state.profileIsSetted)?<Redirect to="/home" />:null}
            </div>
        )    
    }
}

export default UserProfilePage
