import {Component} from 'react'

class TextFormField extends Component
{
    fieldType = ""
    placeHolder = "";
    state = {
        fieldValue:""
    }
    
    constructor(fieldType = "text", placeHolder="Write something", fieldValue="")
    {
        super({fieldType, placeHolder, fieldValue})
        this.fieldType = fieldType
        this.placeHolder = placeHolder

        this.state = {
            fieldValue
        }
    }
    
    handleChange = (event:any) => {
        this.setState({text:event.currentTarget.value})
    }

    render(){
        return (
            <div className = "form-group row">
                <div className = "col-sm-6">    
                    <fieldset>
                        <span>img</span>
                        <input type={this.fieldType} className="form-control" placeholder={this.placeHolder} value={this.state.fieldValue} onChange={this.handleChange} />
                    </fieldset>
                </div>
            </div>
        );
    };
}

export default TextFormField
