import { Component } from "react";
import ActionBar from "./ActionBar/ActionBar";
import ListConversation from './ListConversation/ListConversation';
// import conversations from "./../../../../../wwwroot/data/conversation.json";
import { Credential, UserProfile, ConversationDTO } from "../../../../shared/utils/Types";
import add from "./../../../../../wwwroot/images/plus_white.png"
import NewMessage from './NewMessage/NewMessage';
import NewGroup from './NewGroup/NewGroup';
import NewPrivateChat from './NewPrivateChat/NewPrivateChat';
// import users from "./../../../../../wwwroot/data/users.json"
  
import './LeftSide.css'
import Cookies from "universal-cookie/es6";


class LeftSide extends Component{
    props:any = this.props
    _users: UserProfile[] = []
    _conversations:ConversationDTO[] = []
    cookies =  new Cookies()
    state = {
        conversations: this._conversations ,
        typeConversationIsOpen : false,
        moreConversationIsDisplayed: false,
        newSenderIsChoose: false,
        alertUserFound: "",
        currentDisplayedPage: "conversation" 
    }

    openTypeConversatons = (event:any) => {
        event.currentTarget.style.setProperty("transition", "all linear .05s")
        if(this.state.typeConversationIsOpen === false)
            event.currentTarget.style.setProperty("rotate", "45deg")
        else
            event.currentTarget.style.setProperty("rotate", "0deg")
        this.setState({
                        typeConversationIsOpen: !this.state.typeConversationIsOpen,
                        moreConversationIsDisplayed: !this.state.typeConversationIsOpen,
                        currentDisplayedPage: "conversation"})
    }

    createPrivateChatRoom = (email:string|null, phoneNumber:string|null, chatRoomId:string|number = -1) =>
    {
        var toSend = ""
        if(email !== null && email.trim().toLowerCase().length > 0)
            toSend = `emailAdress=${email}`
        else if(phoneNumber !== null && phoneNumber.trim().length > 0 && email === null)
            toSend = `phoneNumber=${phoneNumber}`
        else if(chatRoomId !== "" && chatRoomId > -1)
            toSend = `userProfileId=${phoneNumber}`
        if(toSend !== "")
        {
            console.log("Sending request")
            this.submitFunction(`https://www.estenligne.com:44364/api/ChatRoom/CreatePrivate?${toSend}`, "POST", this.sucessFunction, this.errorFunction)
        }
        else
            console.log('error')
    }

    sucessFunction = (result:any) => {
        console.log("inside success function")
        var keys = Object.keys(result)
        if(keys.includes('chatRoomId'))
        {
            var identity = result.userProfile.identity
            var name = result.userProfile.username
            var id = result.userProfile.id
            var userChatRoomId = result.id
            this.props.openConversation(identity, id, userChatRoomId, name, "person")
            let elements = document.getElementsByClassName("new-conversation")
            if(elements.length >= 0)
                elements.item(0)?.setAttribute("style", "rotate:0deg;")//?.style.setProperty("rotate", "0deg")
            this.setState({
                typeConversationIsOpen: !this.state.typeConversationIsOpen,
                moreConversationIsDisplayed: !this.state.typeConversationIsOpen,
                currentDisplayedPage: "conversation"})
        }
        else  this.setState({alertUserFound: `Error! something happen wrong ${result}`  })

    } 

    errorFunction = (error:any) => {
        this.setState({alertUserFound: `Error! ${error}`  })
    }

    submitFunction(url:string, type:string, successFunction:Function, errorFunction:Function)
    {
        console.log('submited to ', url, type)
        fetch(url, {
            "method": type,
            "headers": {
                "content-type": "application/json",
                "accept": "application/json"
            },
            }).then(response => response.json())
            .then(
                (result) => {
                    console.log('response is getted')
                    console.log(result)
                    successFunction(result)
                },
                (error) => {
                    console.log('error')
                    errorFunction(error)
                }
            );
    }

    newMessage = (event:any) => {
        this.setState({
            moreConversationIsDisplayed: false, currentDisplayedPage: "newMessagePage"})
    }

    newGroup = (event:any) => {
        this.setState({
            moreConversationIsDisplayed: false, currentDisplayedPage: "newGroupPage"})
    }

    newChatRoom = (event:any) => {
        this.setState({
            moreConversationIsDisplayed: false, currentDisplayedPage: "newPrivateChatRoom"})
    }

    getData(email:string="", phone:string="", id:string|number="")
    {
        return {id:"", email:email, phoneNumber:phone, name:email||phone, category:"person", image:""}
        // return this._users.find((d) => (d.email === email) || (d.id === id) || (d.phoneNumber === phone))
    }

    submitForm = (data:Credential) => {
        let user:UserProfile|undefined = this.getData(`${data.email}`.toLocaleLowerCase().trim(), 
                                                        `${data.phoneNumber}`.toLocaleLowerCase().trim(), "")
        if((data.email !== null || data.phoneNumber != null) && data.password !== "")
            this.setState({newSenderIsChoose: true})
        
        let isEmail = data.email !== undefined && data.email !== "" 
    
        if(user === undefined)
            this.setState({alertUserFound: `No user found with this ${(isEmail)?"email":"phone number"}`})
        else
        {
            try {
                console.log('Request to create chat room')
                this.createPrivateChatRoom(data.email, data.phoneNumber)
            } catch (error) {
                
            }
            
        }    
    }

    handleChange = () => {
        this.setState({alertUserFound: ""})
    }

    render(){
        let newPage = {"newMessagePage":<NewMessage handleChange = {this.handleChange} alertMessage = {this.state.alertUserFound} submitForm = {this.submitForm}/>,  "newGroupPage":<NewGroup submitForm = {this.submitForm}/>, "newPrivateChatRoom":<NewPrivateChat submitForm = {this.submitForm}/>}[this.state.currentDisplayedPage]           
        return(
            <div className="left-side">
                {(newPage !== null && newPage !== undefined)?(newPage):(
                    <>
                        <div >
                            <ActionBar  />
                        </div>
                        <div className = "conversation-list">
                            <ListConversation conversations = {this.state.conversations} openConversation={this.props.openConversation}/>
                        </div>
                    </>)
                }
                <div className = "more-conversations">
                    {(this.state.moreConversationIsDisplayed)?(<div className = "type-conversations">
                        <span onClick = {this.newMessage}> New message </span>
                        <span onClick = {this.newGroup}> New groupe </span>
                        <span onClick = {this.newChatRoom}> Private tchat room</span>
                    </div>):null}
                    <div className = "new-conversation" onClick = {this.openTypeConversatons}>
                        <img src={add} alt="new" />
                    </div>
                </div>
            </div>
        )
    }
}

export default LeftSide
