import  { Component } from 'react'

import {Link} from "react-router-dom";

import TextFormField from '../../shared/textformfield/TextFormField';
import PageName from '../../shared/pagename/PageName';
import FormMessage from '../../shared/formmessage/FormMessage';
import SubmitFormButton from '../../shared/submitformbutton/SubmitFormButton';


import { VerticalLogo } from '../../shared/logo/Logos';

import  "./ForgotPasswordPage.css"

class ForgotPasswordPage extends Component {
    render() {
        return (
            <div className="forgotpassword-page">
                <div className="title" key="forgotpassword-page-name-section">
                    <PageName name="Reset Password" key = "forgotpassword-page-name"/>
                </div>
                <div className = "content" key="forgotpassword-page-tools-sections">
                    <VerticalLogo key = "forgotpassword-page-logo"/>
                    <FormMessage type="error" message="Use either phone or email" key = "forgotpassword-page-alert-message"/>
                    <form>
                        <TextFormField fieldType = "text" placeHolder ="Enter your email or phone number here" />
                        <SubmitFormButton buttonName = "CONFIRM" />
                    </form>
                    <Link to="./../LoginPage" key = "forgotpassword-page-link2" className="fs-4 fw-bold link-secondary">{'>>'} Back to Login {'<<'}</Link>
                </div>
            </div>
        )
    }
}

export default ForgotPasswordPage
