import { Component } from "react";
import LeftSide from "./LeftSide/LeftSide";
import RightSide from './RightSide/RightSide';

import "./Body.css";

import messageList from "./messages.json"

class Body extends Component
{
    props:any = this.props
    msgs:Message[] = [] 
    state = {
        messages:this.msgs,
        email:"",
        id:""
    }
    openDiscution(email:string, id:number|string)
    {
        //select all the message for this discution in the data base
        let messages = messageList.filter(data => filterCondition(data, id, email))
        //map if necessaire 
        this.setState({messages, email, id})
    }

    sendMessage = (event:any, message:string, category="person")=>{
        let date = new Date()
        let datetime = `${date.getFullYear()}-${date.getMonth()}-${date.getDay()}T${date.getHours()}:${date.getMinutes()}`
        let messages = [...this.state.messages];
        let newMessage: Message = {
            email: this.state.email,
            id: this.state.id,
            file:"",
            name: this.props.user.username,
            message: message,
            isRead: false,
            isSend: true,
            isReceive: false,
            isIncomingMessage: false,
            dateTime:datetime,
            category: category
        }
        messages.push(newMessage)
        this.setState({messages})
    }

    render(){
        return(
            <div className="homepage-body">
                <LeftSide openDiscution = {this.openDiscution} key="home-page-right-side"/>
                <RightSide chatIsSelected = {this.state.email !== "" || this.state.id !== "" } messages = {this.state.messages} onSubmit={this.sendMessage} key="home-page-leftside"/>
            </div>
        )
    }
}

function filterCondition(data: { email: string; id: string|number; file: string; name: string; message: string; isRead: boolean; isSend: boolean; isReceive: boolean; isIncomingMessage:boolean, dateTime: string; category: string; }, id: string | number, email: string): unknown {
    return (data.id === id || data.email.toLowerCase().trim() === email.toLowerCase().trim());
}

interface Message {
    email:string,//can be email or id as number
    id:string|number,//can be email or id as number
    file:Blob|string,
    name:string,
    message:string,
    isRead:boolean,
    isSend:boolean,
    isReceive:boolean,
    isIncomingMessage: boolean,
    dateTime:string,
    category:string
}

export default Body
