import { Component } from "react";
import LeftSide from "./LeftSide/LeftSide";
import RightSide from './RightSide/RightSide';

import "./Body.css";

import messageList from "./messages.json"

class Body extends Component
{
    state = {
        messages:[],
        email:"",
        id:""
    }
    openDiscution(email:string, id:number|string)
    {
        //select all the message for this discution in the data base
        let newMessages = messageList.filter(data => filterCondition(data, id, email))
        //map if necessaire 
        let messages = newMessages.map(data => {
            let message:Message = data

            
        }) 
        this.setState({messages, email, id})
    }

    sendMessage = (event:any, message:string)=>{
        let date = new Date()
        let datetime = `${date.getFullYear()}-${date.getMonth()}-${date.getDay()}T${date.getHours()}:${date.getMinutes()}`
        let messages = [...this.state.messages];
        messages.push({"message":message, })
    }

    render(){
        return(
            <div className="homepage-body">
                <LeftSide openDiscution = {this.openDiscution} key="home-page-right-side"/>
                <RightSide messages = {this.state.messages} onSubmit={this.sendMessage} key="home-page-leftside"/>
            </div>
        )
    }
}

function filterCondition(data: { from: string; to: string; file: string; name: string; message: string; isRead: boolean; isSend: boolean; isReceive: boolean; dateTime: string; type: string; }, id: string | number, email: string): unknown {
    return (data.from === id || data.from.toLowerCase().trim() === email.toLowerCase().trim()) && (data.to === id || data.to.toLowerCase().trim() === email.toLowerCase().trim());
}

interface Message {
    from:string|number,//can be email or id as number
    to:string|number,//can be email or id as number
    file:Blob|string,
    name:string,
    message:string,
    isRead:boolean,
    isSend:boolean,
    isReceived:boolean,
    dateTime:string,
    type:string
}

export default Body
