import { Component } from "react";
import Body from "./Body/Body";
import Head from "./Head/Head";
import Cookies from 'universal-cookie';
import { postOrUpdateRequest, getOrDeleteRequest } from "../../shared/utils/functions";
import settings from './../../../settings.json';

import "./HomePage.css"

class HomePage extends Component
{
    cookies = new Cookies()
    settings = settings
    _id:string|number = ""
    state = {
        displayReceiverInformation : false,
        receiverProfile : {
            id : this._id,
            email: "",
            name: "",
            category: "person",
            image: ""
        },
        senderProfile : {
            id : this._id,
            email: "",
            name: "",
            category: "person",
            image: ""
        }
    }

    props:any = this.props

    sucessGetMessages = (response:any) =>{
        console.log(response)
    }

    errorGetMessage = (error:any) =>{

    }

    loadData = () => {
        let userId = this.cookies.get("userId")
    }

    successGetUserProfile = (result:any) => {
        var keys = Object.keys(result)
        if(keys.includes('id') && keys.includes("username"))
        {
            this.setState({senderProfile : {id: result.id, name:result.name, email:result.identity, category:"person", image:""}})
        }
        else
        {
            this.setState({senderProfile : {
                id : this.cookies.get("userId"),
                email: this.cookies.get("userEmail"),
                name: this.cookies.get("userName"),
                category: "person",
                image: ""
            }})
        }
    }

    errorGetUserProfile = (error:any) => {
        // console.log(error)
    }

    componentDidMount = () => {
        //Get userprofileid
        postOrUpdateRequest(`${this.settings.host}/api/DeviceUsed?devicePlatform=4`, "PUT", {}, this.cookies.get('token')??"", (res:any)=>{
            let _keys = Object.keys(res);
            if(_keys.includes("userProfileId"))
            {
                let date = new Date()
                let expirationDate = new Date()
                expirationDate.setMinutes(date.getMinutes() + 7 * 24 * 60)  
                this.cookies.set("userProfileId", res.userProfileId, {sameSite: true, expires:expirationDate})
            }
            else
            {
                //Replace by modal
                alert("Sorry we are not able to get some important information. We inform our team about it.")
            }
        }, (res:any)=>{
            this.setState({alertMessage: "Error something append wrong with query"})
        })
    }

    getReceiverInformation = (email:string, id:number|string, name:string, category:string|number) => {
        if(email !== "" || id !== "" || name !== "")
        {
            this.setState({displayReceiverInformation: true})
            this.setState({receiverProfile: {email, id, name, category, image : ""}})
        }
    }

    render(){
        return (
            <div className="home-page">
                <Head displayReceiverInformation = {this.state.displayReceiverInformation} receiverProfile = {this.state.receiverProfile} senderProfile = {this.state.senderProfile} />
                <Body getReceiverInformation = {this.getReceiverInformation} />
            </div>
        )
    }
}

export default HomePage
