import { Component } from "react";
import ActionBar from "./ActionBar/ActionBar";
import ListConversation from './ListConversation/ListConversation';
import conversations from "./../../../../../wwwroot/data/conversation.json";
import { Credential, UserProfile } from "../../../../shared/utils/Types";
import add from "./../../../../../wwwroot/images/plus_white.png"
import NewMessage from './NewMessage/NewMessage';
import NewGroup from './NewGroup/NewGroup';
import NewPrivateChat from './NewPrivateChat/NewPrivateChat';
import users from "./../../../../../wwwroot/data/users.json"

import './LeftSide.css'


class LeftSide extends Component{
    props:any = this.props

    users: UserProfile[] = [{
        "email":"untester001@gmail.com",
        "phone": "",
        "id":6,
        "name":"Un Tesert",
        "category": "person",
        "image": ""
    },
    {
        "email": "hawaoumarou@gmail.com",
        "phone": "",
        "id": 3,
        "name": "G",
        "category": "person",
        "image": ""
    },
    {
        "email": "dariogattuso@techdgiteasy.it",
        "phone": "",
        "id": 4,
        "name": "Dario G La Tonia",
        "category": "person",
        "image": ""
    },
    {
        "email": "yemelitc@gmail.com",
        "phone": "",
        "id":1,
        "name": "Yemelitc(Yemeli Cyrille Tasse)",
        "category": "person",
        "image": ""
    },
    {
        "email": "estenligne@estenligne.com",
        "phone": "",
        "id": 2,
        "name": "EstEnLigne",
        "category": "group",
        "image": ""
    },
    {
        "email": "bertrandovide@hotmail.com",
        "phone": "+237697927160",
        "id": 5,
        "name": "Ovide Bertrand",
        "category": "person",
        "image": ""
    },
    {
        "email": "cedricnguendap@gmail.com",
        "phone": "",
        "id": 6,
        "name": "Cedric Nguendap",
        "category": "person",
        "image": ""
    },
    {
        "email": "dinalettd@gmail.com",
        "phone": "",
        "id": 7,
        "name": "Dinale Tchinda Takoubo",
        "category": "person",
        "image": ""
    },
    {
        "email": "rostandr@gmail.com",
        "phone": "",
        "id": 8,
        "name": "Rostand Tchouakam",
        "category": "person",
        "image": ""
    },
    {
        "email": "challenge@estenligne.com",
        "phone": "",
        "id": 9,
        "name": "Challenge",
        "category": "person",
        "image": ""
    }]
    state = {
        conversations: conversations,
        typeConversationIsOpen : false,
        moreConversationIsDisplayed: false,
        newSenderIsChoose: false,
        alertUserFound: "",
        currentDisplayedPage: "conversation" 
    }

    openTypeConversatons = (event:any) => {
        event.currentTarget.style.setProperty("transition", "all linear .05s")
        if(this.state.typeConversationIsOpen === false)
            event.currentTarget.style.setProperty("rotate", "45deg")
        else
            event.currentTarget.style.setProperty("rotate", "0deg")
        this.setState({
                        typeConversationIsOpen: !this.state.typeConversationIsOpen,
                        moreConversationIsDisplayed: !this.state.typeConversationIsOpen,
                        currentDisplayedPage: "conversation"})
    }

    newMessage = (event:any) => {
        this.setState({
            moreConversationIsDisplayed: false, currentDisplayedPage: "newMessagePage"})
    }

    newGroup = (event:any) => {
        this.setState({
            moreConversationIsDisplayed: false, currentDisplayedPage: "newGroupPage"})
    }

    newChatRoom = (event:any) => {
        this.setState({
            moreConversationIsDisplayed: false, currentDisplayedPage: "newPrivateChatRoom"})
    }

    getData(email:string="", phone:string="", id:string|number="")
    {
        return this.users.find((d) => (d.email === email) || (d.id === id) || (d.phone === phone))
    }

    submitForm = (data:Credential) => {
        let user:UserProfile|undefined = this.getData(`${data.email}`, `${data.phone}`, "")
        if((data.email !== null || data.phone != null) && data.password !== "")
            this.setState({newSenderIsChoose: true})
        
        let isEmail = data.email !== undefined && data.email !== "" 
        if(user === undefined)
            this.setState({alertUserFound: `No user found with this ${(isEmail)?"email":"phone number"}`})
        else
        {
            this.props.openConversation(user?.email, user?.id, user?.name, user?.category)
            this.setState({
                typeConversationIsOpen: !this.state.typeConversationIsOpen,
                moreConversationIsDisplayed: !this.state.typeConversationIsOpen,
                currentDisplayedPage: "conversation"})
        }    
    }

    handleChange = () => {
        this.setState({alertUserFound: ""})
    }

    render(){
        let newPage = {"newMessagePage":<NewMessage handleChange = {this.handleChange} alertMessage = {this.state.alertUserFound} submitForm = {this.submitForm}/>,  "newGroupPage":<NewGroup submitForm = {this.submitForm}/>, "newPrivateChatRoom":<NewPrivateChat submitForm = {this.submitForm}/>}[this.state.currentDisplayedPage]           
        return(
            <div className="left-side">
                {(newPage !== null && newPage !== undefined)?(newPage):(
                    <>
                        <div >
                            <ActionBar  />
                        </div>
                        <div className = "conversation-list">
                            <ListConversation conversations = {this.state.conversations} openConversation={this.props.openConversation}/>
                        </div>
                    </>)
                }
                <div className = "more-conversations">
                    {(this.state.moreConversationIsDisplayed)?(<div className = "type-conversations">
                        <span onClick = {this.newMessage}> New message </span>
                        <span onClick = {this.newGroup}> New groupe </span>
                        <span onClick = {this.newChatRoom}> Private tchat room</span>
                    </div>):null}
                    <div className = "new-conversation" onClick = {this.openTypeConversatons}>
                        <img src={add} alt="new" />
                    </div>
                </div>
            </div>
        )
    }
}

export default LeftSide
