import { Component, FormEvent } from "react";

import "./ActionBar.css"

class ActionBar extends Component{

    state = {
        fieldValue:""
    }

    handleChange = (event:any) => {
        this.setState({fieldValue: event.currentTarget.value??""})
    }

    handleGetFocus = (event:any) =>{
        let classList = event.currentTarget.classList
        if(classList.contains("text-center"))
            classList.remove("text-center")
    }

    handleLostFocus = (event:any) =>{
        let fieldValue = this.state.fieldValue.trim(); 
        if(fieldValue.length === 0)
        {
            this.setState({fieldValue})
            let classList = event.currentTarget.classList
            if(!classList.contains("text-center"))
                classList.add("text-center")
        }
    }


    render() {
        return (
            <div className="action-bar col col-sm-12">
                <div className="search-icon"><span>img</span></div>
                <div className="col col-sm-10 search-field"><input type="text" className="form-control text-center" onFocus={this.handleGetFocus} onBlur = {this.handleLostFocus} onChange={this.handleChange} value={this.state.fieldValue} placeholder="Search discussion here" /></div>
                <div className="menu-icon"><span className="" >...</span></div>
            </div>
        )
    }
}

export default ActionBar
