function validateEmail(text:string) 
{
    let exp = /^([a-zA-Z0-9_\-.]+)@((\[\d{1,3}\.\d{1,3}\.\d{1,3}\.)|(([a-zA-Z0-9-]+\.)+))([a-zA-Z]{2,4}|\d{1,3})(\]?)$/
    return exp.test(text)
}

function validatePassword(text:string)
{
    return text.length >= 6
}

function validateNumber(text:string) 
{
    let exp = /^((00)|(\+\d+))?(\d{6,})$/
    return exp.test(text)
}

/**
 * 
 * @param url the url
 * @param type the type of query POST, GET, PUT, DELETE
 * @param data Key value object
 * @param successFunction Success function
 * @param errorFunction Error function
 */
 function postOrUpdateRequest(url:string, type:string, data:Object, successFunction:Function, errorFunction:Function)
 {    
     fetch(url, {
         "method": type,
         "headers": {
             "content-type": "application/json",
             "accept": "application/json"
         },
         "body": JSON.stringify(data)
         }).then(response => response.json())
         .then(
             (result) => {
                successFunction(result)
             },
             (error) => {
                errorFunction(error)
             }
         );
 }

 
/**
 * 
 * @param url the endpoint where to send data
 * @param type the type of query GET, DELETE
 * @param successFunction Success function
 * @param errorFunction Error function
 */
 function getOrDeleteRequest(url:string, type:string, successFunction:Function, errorFunction:Function)
 {    
     fetch(url, {
         "method": type,
         "headers": {
             "content-type": "application/json",
             "accept": "application/json"
         },
         }).then(response => response.json())
         .then(
             (result) => {
                successFunction(result)
             },
             (error) => {
                errorFunction(error)
             }
    );
 }

export {validateEmail, validateNumber, validatePassword, postOrUpdateRequest, getOrDeleteRequest}
