import { Component } from "react";

import isWaiting from "./../../../../../../../../wwwroot/images/important_2.png"
import tickDark from "./../../../../../../../../wwwroot/images/tick_dark.png"
import doubleTickDark1 from "./../../../../../../../../wwwroot/images/double_tick_dark.png"
import doubleTickDark2 from "./../../../../../../../../wwwroot/images/double_tick_dark_1.png"
import doubleTickBlue1 from "./../../../../../../../../wwwroot/images/double_tick_blue.png"
import doubleTickBlue2 from "./../../../../../../../../wwwroot/images/double_tick_blue_2.png"
import doubleTickDarkBlue from "./../../../../../../../../wwwroot/images/double_tick_dark_blue.png"

import "./Message.css"


class Message extends Component{
    props:any = this.props
    render(){
        let {type, isIncomingMessage, message, time, status} = this.props
        console.log("status", status)
        return (
            <div className ={`prev-message ${(isIncomingMessage===true)?"incoming-message":"outgoing-message"}`}>
                <div className="prev-message-content">
                    {(type === "file")?<div></div>:<span>{message}</span>}
                </div>
                <div className="sending-information">
                    {
                        (isIncomingMessage === true)?null:<span className="send-image">
                           <img src={outGoingsendImage(status)} alt="" />
                        </span>
                    }
                    <span className="send-datetime">{time}</span>
                </div>
                {/* <div className="failed-sending">
                    {
                        (isIncomingMessage === true)?null:<span className="failed-sending-image">
                           <img src={important2} alt="" />
                        </span>
                    }
                </div> */}
            </div>
        )
    }
}

export default Message

function outGoingsendImage(status:string) {
        switch (status) {
            case "send":
                return tickDark;
            case "received":
                return doubleTickDark2;
            case "read":
                return doubleTickDarkBlue
            default:
                return isWaiting;
    };
}
