function FormMessage(props:any) {
        let type = (""+(props.type??"info")).toLowerCase()
        let textColor = "text-" + ((type === "error")?"danger":type )
        return (<span className={`${textColor} fs-6 fw-bold`}>{props.message}</span>)
}

export default FormMessage
